import React, { useState, useEffect } from "react";
import axios from "axios";
import { urlApiMain } from "../../utils/url/";
import Auth from "../../config/auth";
import { Link, Redirect } from "react-router-dom";

const Register = () => {
  const [alert, setAlert] = useState({
    error: false,
    success: false,
  });

  const [err, setErr] = useState("");

  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
    password_confirmation: "",
  });

  const onInputChange = (event) => {
    const { name, value } = event.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const RegisterUser = () => {
    if (user.password === user.password_confirmation) {
      axios
        .post(`${urlApiMain}register`, user)
        .then((res) => {
          setAlert({
            ...alert,
            ["success"]: true,
          });
        })
        .catch((err) => {
          setErr("The email has already been taken.");
          setAlert({
            ...alert,
            ["error"]: true,
          });
        });
    } else {
      setErr("The password confirmation does not match.");
      setAlert({
        ...alert,
        ["error"]: true,
      });
    }
  };

  return (
    <div className="relative mt-24 h-64">
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="w-1/2 sm:w-1/2 md:w-1/2 lg:w-1/3 xl:w-1/3 h-64">
          <div className="bg-white px-8 pt-6 pb-8 mb-4 rounded overflow-hidden shadow-lg">
            {alert.success ? (
              <div
                className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4"
                role="alert"
              >
                <p className="font-bold">Register Successfull</p>
                <p>You can login with email & password.</p>
                <Link className="text-red-500" to="/login">
                  Login Here
                </Link>
              </div>
            ) : (
              <></>
            )}
            {alert.error ? (
              <div
                className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4"
                role="alert"
              >
                <p className="font-bold">Register Failed</p>
                <p>{err}</p>
              </div>
            ) : (
              <></>
            )}
            <div className="mb-4">
              <label className="bloc text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input
                type="text"
                name="name"
                className="mt-2 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                onChange={(e) => onInputChange(e)}
                placeholder="Ronaldo"
              />
            </div>
            <div className="mb-4">
              <label className="bloc text-gray-700 text-sm font-bold mb-2">
                Email
              </label>
              <input
                type="email"
                name="email"
                className="mt-2 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                onChange={(e) => onInputChange(e)}
                placeholder="example@example.net"
              />
            </div>
            <div className="mb-6">
              <label className="bloc text-gray-700 text-sm font-bold mb-2">
                Password
              </label>
              <input
                type="password"
                name="password"
                className="mt-2 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="password"
                onChange={(e) => onInputChange(e)}
                placeholder="********"
              />
            </div>
            <div className="mb-6">
              <label className="bloc text-gray-700 text-sm font-bold mb-2">
                Repeat Password
              </label>
              <input
                type="password"
                name="password_confirmation"
                className="mt-2 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="password"
                onChange={(e) => onInputChange(e)}
                placeholder="********"
              />
            </div>
            <div className="flex items-center justify-between">
              <button
                onClick={RegisterUser}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline focus:shadow-outline"
                type="button"
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
