const setToken = (data) => {
  localStorage.setItem("user", data);
};

const isAuthenticated = () => {
  let token = JSON.stringify(localStorage.getItem("user"));
  if (token != 'Unauthorized.') {
    return true;
  }else{
    destroyToken();
    return false;
  }
};

const destroyToken = () => {
  localStorage.removeItem("user");
};

const Auth = {
  setToken,
  destroyToken,
  isAuthenticated,
};

export default Auth;
