import React, { useState, useEffect } from "react";
import axios from "axios";
import { urlApiMain, urlWeb } from "../../utils/url";

const EditUser = () => {
  const [profil, setProfil] = useState([]);

  const onInputChange = (event) => {
    const { name, value } = event.target;
    setProfil({
      ...profil,
      [name]: value,
    });
  };

  const getProfil = () => {
    axios
      .get(`${urlApiMain}profil`)
      .then((res) => {
        setProfil(res.data[0]);
      })
      .catch((err) => {});
  };

  useEffect(() => {
    getProfil();
  }, []);

  return (
    <div className="flex justify-center">
      <div className="bg-white md:w-1/4 mt-6 rounded shadow-md px-6 py-6">
        <p className="font-semibold text-lg">Edit Profil</p>
        <div className="flex flex-col items-center">
          <img
            className="w-1/3 flex-1 rounded-full items-center"
            src={`${urlWeb}profil/${profil.photo}`}
          />
          <button className="bg-blue-500 hover:bg-blue-800 text-white font-bold px-4 py-2 rounded-full">
            <i class="fas fa-camera"></i>
          </button>
        </div>
        <label className="bloc text-gray-700 text-sm font-bold mb-2">
          Name
        </label>
        <input
          type="text"
          name="name"
          value={profil.name}
          className="mt-2 mb-4 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          onChange={(e) => onInputChange(e)}
          placeholder="Robert"
        />
        <label className="bloc text-gray-700 text-sm font-bold mb-2">
          Email
        </label>
        <input
          type="email"
          name="email"
          value={profil.email}
          className="mt-2 mb-4 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          onChange={(e) => onInputChange(e)}
          placeholder="Robert@gmail.com"
        />
        <label className="bloc text-gray-700 text-sm font-bold mb-2">
          Password
        </label>
        <input
          type="password"
          name="password"
          className="mt-2 mb-4 shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          onChange={(e) => onInputChange(e)}
          placeholder="*********"
        />
      </div>
    </div>
  );
};

export default EditUser;
