import React, { useEffect, useState } from "react";
import AutoComplete from "../../components/autocomplete";
import axios from "axios";
import { urlApiMain } from "../../utils/url";
import { Link } from "react-router-dom";
import { Auth } from "../../config";

const CountUnreadMessage = (props) => {
  const { sender, receiver } = props;
  const [count_unreadMessage, setUnreadMessage] = useState("");

  const unreadMessage = (sender, receiver) => {
    axios
      .get(`${urlApiMain}countunreadmessage/${sender}/${receiver}`)
      .then((res) => {
        setUnreadMessage(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    unreadMessage(sender, receiver);
  }, []);

  return <div className="bg-green-400 rounded-full absolute right-0">{count_unreadMessage}</div>;
};

const Home = () => {
  const [userToTalk, setUserToTalk] = useState([]);
  const [userLatestMessages, setUserLatestMessages] = useState([]);

  const letsMessageWithPeople = () => {
    axios
      .get(`${urlApiMain}letmessage`)
      .then((res) => {
        setUserToTalk(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const latestMessages = () => {
    axios
      .get(`${urlApiMain}latestmessage`)
      .then((res) => {
        setUserLatestMessages(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    letsMessageWithPeople();
    latestMessages();
  }, []);

  return (
    <div className="flex justify-center">
      <div className="bg-white md:w-1/2 mt-6 rounded shadow-lg">
        <div className="px-6 py-6">
          <AutoComplete suggestions={userToTalk.map((user) => user.name)} />
          <ul>
            {userLatestMessages.map((latestUser) => (
              <li className="flex flex-row">
                <Link className="text-gray-700" to={`/chat/${latestUser.id}`}>
                  {latestUser.name}
                </Link>
                <CountUnreadMessage
                  sender={latestUser.id}
                  receiver={Auth.getTokenAndUserId().userid}
                />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Home;
