const setToken = (token, expired) => {
  localStorage.setItem("token", token);
  localStorage.setItem("expired", expt);
};

const getToken = () => {
  let token = localStorage.getItem("token");
  let expired = localStorage.getItem("expired");
  if (!token || !expired) {
    return null;
  }
  if (Date.now() > parseInt(expired)) {
    destroyToken();
  } else {
    return token;
  }
};

const destroyToken = () => {
  localStorage.removeItem("token");
  localStorage.removeItem("expired");
};

const isAuthenticated = () => {
  if (getToken()) return true;
  else return false;
};

const Auth = {
  setToken,
  getToken,
  destroyToken,
  isAuthenticated,
};

export default Auth;
