import React, {useEffect} from "react";
import "./assets/main.css";
import { Login, Register, Home, ChatRoom } from "./pages/index";

// Library
// Router Navigation
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import { Auth } from "./config";
import axios from "axios";

function App() {
  if (Auth.isAuthenticated()) {
    axios.defaults.headers.common["Authorization"] =
      "Bearer " + Auth.getTokenAndUserId().token;

  }

  return (
    <Router>
      <Switch>
        <Route
          path="/login"
          render={() =>
            !Auth.isAuthenticated() ? <Login /> : <Redirect to="/" />
          }
        />
        <Route
          path="/register"
          render={() =>
            !Auth.isAuthenticated() ? <Register /> : <Redirect to="/" />
          }
        />
        <Route
          path="/"
          exact
          render={() =>
            Auth.isAuthenticated() ? <Home /> : <Redirect to="/login" />
          }
        />
        <Route
          path="/chat/:id"
          exact
          render={({match}) =>
            Auth.isAuthenticated() ? <ChatRoom /> : <Redirect to="/login" />
          }
        />
      </Switch>
    </Router>
  );
}

export default App;
