import React, { useEffect, useState } from "react";
import axios from "axios";
import Moment from "react-moment";
import { useParams } from "react-router-dom";
import { urlApiMain } from "../../utils/url";
import { Auth } from "../../config";

const ChatRoom = () => {
  const { id } = useParams();
  const [messages, setMessages] = useState([]);
  const [sendmessage, setSetmessage] = useState({
    messages: "",
    image: "",
    receiver: id,
  });

  const getChat = () => {
    axios
      .get(urlApiMain + "getchat")
      .then((res) => {
        setMessages(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const onChangeInput = (event) => {
    const { name, value } = event.target;
    setSetmessage({
      ...sendmessage,
      [name]: value,
    });
    console.log(sendmessage);
  };

  const getFile = () => {
    document.getElementById("fileImage").click();
  }

  const clearChat = () => {
    setSetmessage({
      ...sendmessage,
      ["messages"]: "",
    });
  };

  const sendMessage = () => {
    axios
      .post(urlApiMain + "sendchat", sendmessage)
      .then((res) => {
        console.log(res);
        getChat();
        clearChat();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getChat();
  }, []);

  return (
    <div className="flex justify-center">
      <div className="bg-white md:w-1/2 mt-4 mb-6 rounded shadow-lg">
        <div className="px-6 py-6">
          <h2 className="font-semibold">
            {" "}
            <i class="fas fa-arrow-left"></i> Chat With By User
          </h2>
          <div className="bg-gray-200 message-height px-3 py-2 overflow-auto">
            {messages.map((message) =>
              message.sender == Auth.getTokenAndUserId().userid ? (
                <div className="flex flex-col items-end mb-3">
                  <div className="w-1/2 px-3 py-2 right-0 rounded-tl-lg rounded-br-lg bg-green-300">
                    <p className="text-gray-800">{message.messages}</p>
                    <p className="text-gray-700 font-bold text-sm">
                      <Moment fromNow>{message.created_at}</Moment>
                    </p>
                  </div>
                </div>
              ) : (
                <div className="flex flex-col items-start mb-3">
                  <div className="w-1/2 px-3 py-2 right-0 rounded-tr-lg rounded-bl-lg bg-orange-300">
                    <p className="text-gray-800">{message.messages}</p>
                    <p className="text-gray-700 font-bold text-sm">
                      <Moment fromNow>{message.created_at}</Moment>
                    </p>
                  </div>
                </div>
              )
            )}
          </div>
          <div className="flex mt-3">
            <input
              name="messages"
              value={sendmessage.messages}
              onChange={(e) => onChangeInput(e)}
              className="appearance-none w-full bg-gray-200 text-gray-700 border border-gray-200 rounded-full 
              mr-4 py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
              type="text"
              placeholder="Type message here...."
            />
            {/* <!-- Image Button Picker --> */}
            <input type="file" id="fileImage" className="hidden" />
            <button onClick={getFile} className="bg-green-400 hover:bg-green-700 text-white font-bold px-4 py-3 mr-1 rounded-full">
              <i class="fas fa-camera"></i>
            </button>
            <button
              onClick={sendMessage}
              className="bg-blue-400 hover:bg-blue-700 text-white font-bold px-4 py-3 rounded-full"
            >
              <i class="fas fa-paper-plane"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatRoom;
